private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
// TODO add your handling code here:
    try {
            Class.forName("org.postgresql.Driver");
            Connection cn = DriverManager.getConnection("jdbc:postgresql://localhost/postgres","postgres","dbms");
            Statement stmt = cn.createStatement();
    
            String fn = jTextField1.getText();
            String ln = jTextField2.getText();
            String factid = jTextField3.getText();
            String shift = jTextField4.getText();
            String seva = jTextField5.getText();
            String dept = jTextField6.getText();
    
            String insert_query = "Insert into faculty_reg values(?,?,?,?,?,?)";
            PreparedStatement ps = cn.prepareStatement(insert_query);
            ps.setString(1, fn);
                       
            ps.setString(2, ln);
            
            ps.setString(3, factid);
            
            ps.setString(4, dept);
            
            ps.setString(5, shift);
            
            ps.setString(6, seva);
            ps.executeUpdate();           
            
            ResultSet res = stmt.executeQuery(insert_query);
    }
    catch (Exception e) {
        System.out.println(" " + e);
    }
    
    
    fact_success ffr = new fact_success();
     ffr.setVisible(true);
     this.dispose();
    
}

  

